sp_widget.client_script:
api.controller=function(spUtil, $rootScope) {
  /* widget controller */
  var c = this;
	c.submitting = false;
	c.submit = function(){
		if(c.data.newTask.name == "" ||
			c.data.newTask.desc == "" ||
			 c.data.newTask.expense == "" ||
			 c.data.newTask.start_date == "" ||
			 c.data.newTask.end_date == "" ||
			  c.data.newTask.project == "" ||
			 c.data.newTask.assignedto == "" ||
			 c.data.newTask.bug == "" 
			){
			spUtil.addErrorMessage("All the fields must be filled");
			return;
			 }


		console.log(c.data.newTask.start_date);
		console.log(c.data.newTask.end_date);

		c.data.action = "createTask";
		c.submitting = true;
		c.server.update().then(function(){
			c.submitting = false;
			spUtil.addInfoMessage(c.data.message);
		})
	}
};

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	data.newTask = {
		name:"",
		desc:"",
		expense: "",
		start_date: "",
		end_date: "",
		project: "",
		assignedto: "",
		bug: ""
	}

	//data.items = new global.PortalUtils().getProjects();
  data.bugs = new global.PortalUtils().getBugs();
	data.project = new global.PortalUtils().getProjects();
	data.users = new global.PortalUtils().getDevUsers();
	//Create new bug
	if(input && input.action =="createTask"){

		var before = new GlideDateTime(data.start_date);
		var after = new GlideDateTime(data.end_date);
		var taskutils = new TaskUtils();
		var isPast = taskutils.isDatePast(data.start_date);
		data.start_date = before;
		data.end_date = after;
		var proceed=true;



		// If the isDatePast method returns true, the date is in the past.
		if(isPast == true){
			 data.message = "Start date cannot be in the past.  Your Task has not been saved to the database.";
				proceed = false;
		}
		if(proceed){
			new global.PortalUtils().createScrumTask(input.newTask);
			data.message = "A Scrum Task has been created";
		}

	}
})();
